fun findValidPasswords(passwords: List<String>): Int {
    var count = 0
    
    passwords.forEach {
        val rules = getRules(it.split(":").first())
        val min = rules.first
        val max = rules.second
        val letter = rules.third

        val password = it.split(":").last()
        val numOccurences = password.filter { it == letter }.count()
        
        if (numOccurences >= min && numOccurences <= max) count++
    }
    
    
    return count
}

fun getRules(rules: String): Triple<Int, Int, Char> {
    val numOccurences = rules.split(" ").first()
    val letter = rules.split(" ").last()
    val minOccurences = numOccurences.split("-").first().toInt()
    val maxOccurences = numOccurences.split("-").last().toInt()
    
    return Triple(minOccurences, maxOccurences, letter.first())
}