fun countTrees(skiMap: List<String>): Int {
    var count = 0
    var xPos = 0
    var yPos = 0
    
    skiMap.forEach {
        if (xPos < it.length - 3) xPos += 3 else xPos = xPos + 3 - it.length // wrap around if we've reached the edge of the map
        if (yPos < skiMap.size - 1) yPos += 1 else return count
        if (skiMap[yPos][xPos] == '#') count++
    }

    return -1.also { "you've met with a terrible fate" }
}
