fun passports(batchPassports: String): Int {
    
    var count = 0
    val requiredFields = listOf("byr:(19[2-9][0-9]|200[0-2])(\\s|\n)".toRegex(), 
                                "iyr:(201[0-9]|2020)(\\s|\n)".toRegex(), 
                                "eyr:(202[0-9]|2030)(\\s|\n)".toRegex(), 
                                "hgt:(1([5-8][0-9]|9[0-3])cm|(59|6[0-9]|7[0-6])in)(\\s|\n)".toRegex(), 
                                "hcl:#([0-9]|[a-f]){6}(\\s|\n)".toRegex(), 
                                "ecl:(amb|blu|brn|gry|grn|hzl|oth)(\\s|\n)".toRegex(), 
                                "pid:[0-9]{9}(\\s|\n)".toRegex())
    
    val splitString = batchPassports.split("\n\r\n").toList()
    
    splitString.forEach {
        val passport = it
        var valid = true
        requiredFields.forEach {
            if (!passport.contains(it)) valid = false
        }
        if (valid) count++
    }
    
    return count
}

